$CTM_TEMPLATE_DATABASE['sidebarServerList'] = <<<HTML
<if syntax="::x####$####x::this->settings['SERVERLIST']['SWITCH'] == true && count(::x####$####x::global_serverlist['servers']) > 0">
            <script type="text/javascript">
            loadServers = new Array();
            <foreach loop="::x####$####x::global_serverlist['servers'] as ::x####$####x::key => ::x####$####x::value">
            loadServers['{::x####$####x::key}'] = {CURRENT_STYLE: ".green-gauge", COUNT: {::x####$####x::value['count']}};
            </foreach>
            ::x####$####x::(document).ready(function(e)
            {
                if(e)
                {
                    for(GS_ID in loadServers)
                    {
                        var setStyle = ".green-gauge";
                        if(loadServers[GS_ID]['COUNT'] >= 80) setStyle = ".red-gauge";
                        
                        ::x####$####x::("#ServerID_GS-"+GS_ID+" .green-gauge").attr("class", setStyle.replace(".", ""));
                        loadServers[GS_ID]['CURRENT_STYLE'] = setStyle;
                    }
                }
            });
            
            function updateServerCount(GS_ID, Count)
            {
                var setStyle = ".green-gauge";
                if(Count >= 80) setStyle = ".red-gauge";
                
                ::x####$####x::("#GS-"+GS_ID+"_Count").html(Count+"%");
                ::x####$####x::("#ServerID_GS-"+GS_ID+" "+loadServers[GS_ID]['CURRENT_STYLE']).css("width", Count+"%");
                ::x####$####x::("#ServerID_GS-"+GS_ID+" "+loadServers[GS_ID]['CURRENT_STYLE']).attr("class", setStyle.replace(".", ""));
                
                loadServers[GS_ID] = {CURRENT_STYLE: setStyle, COUNT: Count};
            }
            </script>
            </if>
            <div id="ServerRefresh" style="display: none;"></div>
            <ul class="info">
                <if syntax="::x####$####x::this->settings['SERVERLIST']['SWITCH'] == true && count(::x####$####x::global_serverlist['servers']) > 0">
                <foreach loop="::x####$####x::global_serverlist['servers'] as ::x####$####x::key => ::x####$####x::value">
                <li>
                    <span>{::x####$####x::value['name']} : </span>
                    <a href="javascript: void(0);" onclick="CTM.Load('?/usersonline/room-{::x####$####x::key}','content');">
                        <strong id="GS-{::x####$####x::key}_Count">{::x####$####x::value['count']}%</strong>
                    </a>
                    <div class="border-bar" id="ServerID_GS-{::x####$####x::key}">
                        <div class="green-gauge" style="width: {::x####$####x::value['count']}%"><!-- Bar --></div>
                    </div>
                </li>
                </foreach>
                <li class="line"><!-- Separator line --></li>
                </if>
                <li>
                    <span>{::x####$####x::this->lang->words['Sidebar']['ServerList']['TotalOnline']} </span>
                    <a href="javascript: void(0);" onclick="CTM.Load('?/usersonline','content');"><strong id="TotalOnline">{::x####$####x::global_serverlist['totalOnline']}</strong></a>
                    <a href="javascript: void(0);" onclick="CTM.AjaxLoad('?app=core&amp;ajax=refreshServers', 'ServerRefresh');">
                        <img src="{::x####$####x::this->vars['board_url']}{::x####$####x::this->vars['style_dirs']['images']}icons/refresh.png" width="10" height="10" border="0">
                    </a>
                </li>
            </ul>
HTML;
