	<div class="header"><span>{::x####$####x::this->lang->words['Rankings']['LoadRank']['Titles']['PkKills']}</span></div>
    <if syntax="count(::x####$####x::ranking_result) > 0">
    <script type="text/javascript">
	::x####$####x::(document).ready(function()
	{
		::x####$####x::(".CharLink").click(function()
		{
			var charName = encodeURIComponent(::x####$####x::(this).attr("rel"));
			CTM.Load('?/char/'+charName,'content');
		});
		::x####$####x::(".GuildLink").click(function()
		{
			var guildName = encodeURIComponent(::x####$####x::(this).attr("rel"));
			CTM.Load('?/guild/'+guildName,'content');
		});
	});
	</script>
    <ul class="rank">
    	<foreach loop="::x####$####x::ranking_result as ::x####$####x::rank => ::x####$####x::infos">
    	<li>
        	<img src="{::x####$####x::infos['image']}" width="50" height="50">
            <p><strong>{::x####$####x::rank}ยบ - </strong><a href="javascript: void(0);" class="CharLink" rel="{::x####$####x::infos['name']}">{::x####$####x::infos['name']}</a></p>
            <p><strong>{::x####$####x::this->lang->words['Rankings']['LoadRank']['Results']['Kills']} </strong><span class="blue">{::x####$####x::infos['result']}</span></p>
            <p><strong>{::x####$####x::this->lang->words['Rankings']['LoadRank']['Guild']} </strong><a href="javascript: void(0);" class="GuildLink" rel="{::x####$####x::infos['secondary']}">{::x####$####x::infos['secondary']}</a></p>
    	</li>   
        </foreach>     
	</ul>
    <else />
    <div class="error-box">{::x####$####x::this->lang->words['Rankings']['LoadRank']['Messages']['NotResult']}</div>
    </if>
</div>
HTML;
/********** End: ranking_pkKills **********/

