	/* headerTemplate : 0x00 */
	private function loop__headerTemplate__0x00()
	{
		$content = NULL;

		if(count($this->settings['WEBPUBLIC']['TEMPLATES']) > 0)
		{
			foreach($this->settings['WEBPUBLIC']['TEMPLATES'] as $key => $value)
			{
		$selected = $this->output->templateId == $key ? " selected=\"selected\"" : NULL;
		$content .= "<option value=\"{$key}\"{$selected}>{$value[1]}</option>
					";
			}
		}
		return rtrim($content);
	}
	/* headerTemplate : 0x01 */
	private function loop__headerTemplate__0x01()
	{
		$content = NULL;

		if(count($this->settings['WEBPUBLIC']['LANGUAGES']) > 0)
		{
			foreach($this->settings['WEBPUBLIC']['LANGUAGES'] as $key => $value)
