		}
		return rtrim($content);
	}
	/* sidebarServerList : 0x02 */
	private function loop__sidebarServerList__0x02()
	{
		global $global_serverlist; // PHP Globals
		$content = NULL;

		if(count($global_serverlist['servers']) > 0)
		{
			foreach($global_serverlist['servers'] as $key => $value)
			{
				$content .= "loadServers['{$key}'] = {CURRENT_STYLE: \".green-gauge\", COUNT: {$value['count']}};
            ";
