		}
		return rtrim($content);
	}
	/* sidebarServerList : 0x03 */
	private function loop__sidebarServerList__0x03()
	{
		global $global_serverlist; // PHP Globals
		$content = NULL;

		if(count($global_serverlist['servers']) > 0)
		{
			foreach($global_serverlist['servers'] as $key => $value)
			{
				$content .= "<li>
                    <span>{$value['name']} : </span>
                    <a href=\"javascript: void(0);\" onclick=\"CTM.Load('?/usersonline/room-{$key}','content');\">
                        <strong id=\"GS-{$key}_Count\">{$value['count']}%</strong>
                    </a>
                    <div class=\"border-bar\" id=\"ServerID_GS-{$key}\">
                        <div class=\"green-gauge\" style=\"width: {$value['count']}%\"><!-- Bar --></div>
                    </div>
                </li>
                ";
			}
		}
		return rtrim($content);
	}
