	private function loop__rankingPage__0x00()
	{
		$content = NULL;

		if(count($this->settings['RANKING']['GENERATOR']['LIMIT']) > 0)
		{
			foreach($this->settings['RANKING']['GENERATOR']['LIMIT'] as $limit)
			{
				$content .= "<option value=\"{$limit}\">{$limit} {$this->lang->words['Rankings']['Generator']['Results']}</option>
            	";
			}
		}
		return rtrim($content);
	}
